local setmetatable = setmetatablemodule( "facepunch.session" )--------------------------------------------------------------------------------- session-- Purpose: Class index-------------------------------------------------------------------------------local session = {}--------------------------------------------------------------------------------- __metatable-- Purpose: Class metatable-------------------------------------------------------------------------------__metatable = {	__index = session,	__type = "session"}--------------------------------------------------------------------------------- session.new()-- Purpose: Creates a new session object-- Output: session-------------------------------------------------------------------------------function new()	local t = {		username = nil,		passwordHash = nil	}	setmetatable( t, __metatable )	return tend--------------------------------------------------------------------------------- session()-- Purpose: Shortcut to session.new()-- Output: session-------------------------------------------------------------------------------local metatable = {	__call = function( _, ... )		return new( ... )	end}setmetatable( _M, metatable )--------------------------------------------------------------------------------- member:__eq( session )-- Purpose: Compares against another session object-- Output: boolean-------------------------------------------------------------------------------function session:__eq( session )	if not self.username then return false end	return self.username == session.usernameend--------------------------------------------------------------------------------- member:__tostring()-- Purpose: Returns a string representation of a member-- Output: string-------------------------------------------------------------------------------function __metatable:__tostring()	if not self.username then return "invalid session" end	return "member: " .. self.usernameend